// n8n Code Node - Format Biology Questions Data

try {
  // Parse the nested JSON string
  const parsedData = JSON.parse($input.item.json.Data);

  // Extract the questions array
  const questions = parsedData.results[0].questions;

  // Format each question into a clean structure
  const formattedQuestions = questions.map((q, index) => {
    const englishQuestion = q.question.en;

    // Helper function to safely convert timestamp
    const formatTimestamp = (timestamp) => {
      if (!timestamp) return null;
      try {
        const date = new Date(timestamp * 1000);
        return isNaN(date.getTime()) ? null : date.toISOString();
      } catch (e) {
        return null;
      }
    };

    return {
      questionNumber: index + 1,
      questionId: q.question_id,

      // Exam Information
      exam: q.exam.toUpperCase(),
      paperTitle: q.paperTitle,
      year: q.year,

      // Subject Information
      subject: q.subject,
      chapterGroup: q.chapterGroup,
      chapter: q.chapter.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
      topic: q.topic ? q.topic.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase()) : null,

      // Question Details
      type: q.type.toUpperCase(),
      marks: q.marks,
      negativeMarks: q.negMarks,
      timeAllotted: q.timeAllotted / 1000 + ' seconds',
      difficulty: q.difficulty,

      // Question Content
      questionText: englishQuestion.content.trim(),
      options: englishQuestion.options,
      correctAnswer: englishQuestion.correct_options,
      explanation: englishQuestion.explanation ? englishQuestion.explanation.trim() : null,

      // Metadata
      permalink: q.permalink,
      isBonus: q.isBonus,
      isOutOfSyllabus: q.isOutOfSyllabus,
      updatedTime: formatTimestamp(q.updated_time)
    };
  });

  // Return formatted data
  return formattedQuestions.map(q => ({ json: q }));

} catch (error) {
  throw new Error(`Data formatting failed: ${error.message}`);
}
